#include <bits/stdc++.h>
using namespace std;

long long mod = 1000000007;

void solve() {
  long long n;
  cin >> n;
  long long ans = (n * (n - 1)) / 2 - n;
  cout << (ans % mod) << '\n';
}
signed main() {
  ios_base::sync_with_stdio(0);
  cin.tie(0);
  cout.tie(0);
  long long _t;
  cin >> _t;
  while (_t--) solve();
}
```
