#include <bits/stdc++.h>
using namespace std;

#define ll long long int

int main()
{
    ios_base::sync_with_stdio(false);
    cin.tie(NULL);
    cout.tie(NULL);

    ll n, k;
    cin >> n >> k;

    ll a[n + 1], sum = 0, first_idx[k], ans = 0;

    memset(first_idx, -1, sizeof(first_idx));

    for (int i = 0; i < n; i++)
        cin >> a[i + 1];

    first_idx[sum] = 0;

    for (int i = 1; i <= n; i++)
    {
        (sum += a[i]) %= k;
        sum = (sum + k) % k;

        if (first_idx[sum] == -1)
            first_idx[sum] = i;
        else
            ans = max(ans, i - first_idx[sum]);
    }

    cout << ans << '\n';
}
